                setHvac = 'fan_only'
            } else {
                setHvac = 'off'
            }
            if (hotDay === 'on') {
                setEco = 'off'
            } else {
                setEco = 'on'
            }
        }
    }
// Night Time
} else if (time === 'night') {
    if (type === 'auto') {
        setCool = 'turn_on'
    }
    if (danger === 'Extreme') {
        setTemp = bedTemp
    } else {
        setTemp = nightTemp
    }
