    } else if (fanMode === 'Fan') {
        setFan = 'turn_on'
    }
// Bed Time
} else if (time === 'bedtime') {
    setPeople = 'turn_off'
    if (ac === 'on') {
        setTemp = bedTemp
        setEco = 'none'
        if (acMode === 'AC') {
            setHvac = 'cool'
        } else if (acMode === 'Fan') {
            setHvac = "fan_only"
        } else {
            setHvac = "off"
        }
    }
    if (fanMode === 'Fan') {
        setFan = 'turn_on'
    } else {
        setFan = 'turn_off'
    }
